import { GENRE_LIST_FAIL, GENRE_LIST_REQUEST, GENRE_LIST_SUCCESS } from "../constatns/genreConstants";

export const listGenres = () => async (dispatch) => {
    try {
        dispatch({ type: GENRE_LIST_REQUEST });

        const { data } = await axios.get('/api/genres');

        dispatch({
            type: GENRE_LIST_SUCCESS,
            payload: data
        })
    } catch (error) {
        dispatch({
            type: GENRE_LIST_FAIL,
            payload: error.response && error.response.data.message ? error.response.data.message : error.message
        })
    }
}
