import React, { useEffect, useState } from "react";
import { Collapse, Card, Col, Row, Button, Dropdown } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getOrderDetails } from "../actions/orderActions";
import Loader from "../components/Loader";
import Message from "../components/Message";

const OrderScreen = ({ match }) => {
  const [open, setOpen] = useState(false);
  const orderId = match.params.id;
  const dispatch = useDispatch();

  const orderDetails = useSelector((state) => state.orderDetails);
  const { order, loading, error } = orderDetails;

  useEffect(() => {
    dispatch(getOrderDetails(orderId));
  }, [dispatch, orderId]);

  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <>
      <Row className="justify-content-center">
        <Col lg={7}>
          <Card bg="danger p-1">
            <Row className="justify-content-center">
              <Col
                lg={10}
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
              >
                <h3>Order: {order._id}</h3>
              </Col>
            </Row>
            <Row className="justify-content-center py-2">
              <Col lg={4} style={{ textAlign: "center" }} as="h5">
                Product:{" "}
              </Col>
              <Col lg={6} style={{ textAlign: "left" }} as="h6">
                {" "}
                {order.orderItem.title}{" "}
              </Col>
            </Row>
            <Row className="justify-content-center py-2">
              <Col lg={4} style={{ textAlign: "center" }} as="h5">
                Account:{" "}
              </Col>
              <Col lg={6} style={{ textAlign: "left" }} as="h5">
                {" "}
                {order.user.name}{" "}
              </Col>
            </Row>
            <Row className="justify-content-center py-2">
              <Col lg={4} style={{ textAlign: "center" }} as="h5">
                Price:{" "}
              </Col>
              <Col lg={6} style={{ textAlign: "left" }} as="h5">
                {" "}
                ${order.orderItem.price}{" "}
              </Col>
            </Row>
            <Row className="justify-content-center py-2">
              <Col lg={4} style={{ textAlign: "center" }} as="h5"></Col>
              <Row className="justify-content-center">
                <Col
                  lg={7}
                  className="py-3"
                  style={{
                    borderTop: "solid",
                    borderWidth: "0.5px",
                    textAlign: "center",
                  }}
                >
                  <Button
                    variant="dark"
                    style={{
                      width: "40%",
                    }}
                    onClick={() => setOpen(!open)}
                    aria-controls="example-collapse-text"
                    aria-expanded={open}
                  >
                    click
                  </Button>
                  <Collapse in={open} className="my-1 ">
                    <Dropdown>
                      <h5 style={{ color: "black" }}>KEY</h5>
                    </Dropdown>
                  </Collapse>
                </Col>
              </Row>
            </Row>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default OrderScreen;
