import React, { useEffect } from "react";
import { Card, Col, ListGroup, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Game from "../components/Game";
import { listGames } from "../actions/gameActions";
import Loader from "../components/Loader";

const AllGamesScreen = () => {
  const gameList = useSelector((state) => state.gameList);
  const { loading, error, games } = gameList;
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(listGames(""));
  }, [dispatch]);

  return (
    <div>
      <Row>
        <Col lg={2}>
          <ListGroup>
            <ListGroup.Item>1</ListGroup.Item>
            <ListGroup.Item>2</ListGroup.Item>
            <ListGroup.Item>3</ListGroup.Item>
            <ListGroup.Item>4</ListGroup.Item>
          </ListGroup>
        </Col>
        {loading && <Loader />}
        <Col>
          <Row
            style={{ borderBottom: "solid", borderWidth: "0.5px" }}
            className="justify-content-mid"
          >
            <Col>
              {games.map((game) => (
                <Game game={game} />
              ))}
            </Col>
          </Row>
          <Row style={{ borderBottom: "solid", borderWidth: "0.5px" }}>
            {games.map((game) => (
              <Game game={game} />
            ))}
          </Row>
          <Row style={{ borderBottom: "solid", borderWidth: "0.5px" }}>
            {games.map((game) => (
              <Game game={game} />
            ))}
          </Row>
        </Col>
      </Row>
    </div>
  );
};

export default AllGamesScreen;
