import React, { useEffect } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { listGames } from "../actions/gameActions";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { Button, Card, Col, Row, Table } from "react-bootstrap";

const GameListScreen = ({ history, match }) => {
  const dispatch = useDispatch();

  const gameList = useSelector((state) => state.gameList);
  const { loading, error, games } = gameList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(listGames());
    } else {
      history.push("/login");
    }
  }, [dispatch, userInfo, history]);

  const deleteHandler = (id) => {
    if (window.confirm("Are you sure")) {
      //Delete game
    }
  };

  const createProduchHandler = () => {
    console.log("create");
  };

  return (
    <>
      <Row
        className="my-3"
        style={{ borderBottom: "solid", borderWidth: "0.5px" }}
      >
        <Col className="">
          <h1>Games</h1>
        </Col>
        <Col className="text-right">
          <Button className="" onClick={createProduchHandler}>
            <i className="fas fa-plus"></i>&nbsp; Create Game
          </Button>
        </Col>
      </Row>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Row className="justify-content-center">
          <Col lg={11} md={15}>
            <Card className="p-1" style={{ backgroundColor: "darkblue" }}>
              <Table striped borderless hover responsive className="table-md">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>TITLE</th>
                    <th>GENRE</th>
                    <th>PRICE</th>
                    <th>STOCK</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {games.map((game) => (
                    <tr key={game._id}>
                      <td>{game._id}</td>
                      <td>{game.title}</td>
                      <td>{game.genre.name}</td>
                      <td>${game.price}</td>
                      <td>{game.numberInStock}</td>
                      <td>
                        <LinkContainer
                          to={`/admin/game/${game._id}/edit`}
                          className="mx-1"
                        >
                          <Button variant="dark" className="btn-sm">
                            <i className="fas fa-edit"></i>
                          </Button>
                        </LinkContainer>
                        <Button
                          variant="danger"
                          className="btn-sm"
                          onClick={() => deleteHandler(game._id)}
                        >
                          <i className="fas fa-trash"></i>
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card>
          </Col>
        </Row>
      )}
    </>
  );
};

export default GameListScreen;
