import { CART_ADD_ITEM, CART_REMOVE_ITEM } from '../constatns/cartConstants'

export const cartReducer = (state = { cartItems: [] }, action) => {
    switch (action.type) {
        case CART_ADD_ITEM:
            const item = action.payload;

            const existItem = state.cartItems;

            if (existItem) {
                return {
                    ...state,
                    cartItem: state.cartItems === existItems.game ? item : cartItem
                }
            } else {
                return {
                    ...state,
                    cartItems: item
                }
            }
        default:
            return state;
    }
}
