import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Col, Row, Carousel, Image, Container, Button } from "react-bootstrap";
import Game from "../components/Game";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { Link } from "react-router-dom";
import { listGames } from "../actions/gameActions";
import ItemsCarousel from "react-items-carousel";

const HomeScreen = ({ match }) => {
  const dispatch = useDispatch();
  const [activeItemIndex, setActiveItemIndex] = useState(0);
  const chevronWidth = 20;
  const keyword = match.params.keyword;

  const gameList = useSelector((state) => state.gameList);
  const { loading, error, games } = gameList;
  const discountGames = [];
  games.forEach((game) => (game.price <= 10 ? discountGames.push(game) : null));
  useEffect(() => {
    dispatch(listGames(keyword));
  }, [dispatch, keyword]);

  return (
    <>
      <h1>Latest Games</h1>
      <Container>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <>
            <Row className="justify-content-center">
              <Col>
                <Carousel>
                  {discountGames.map((game) => (
                    <Carousel.Item key={game._id}>
                      <Link
                        to={`/game/${game._id}`}
                        style={{ textDecoration: "none" }}
                      >
                        <Image
                          src={game.image}
                          alt={game.title}
                          className="d-block w-100"
                          fluid
                        />
                      </Link>
                    </Carousel.Item>
                  ))}
                </Carousel>
              </Col>
            </Row>
            <Row className="justify-content-center">
              <Col className="text-center">
                <ItemsCarousel
                  requestToChangeActive={setActiveItemIndex}
                  activeItemIndex={activeItemIndex}
                  numberOfCards={3}
                  gutter="30"
                  leftChevron={<Button>{"<"}</Button>}
                  rightChevron={<Button>{">"}</Button>}
                  outsideChevron
                  chevronWidth={chevronWidth}
                >
                  {games.map((game) => (
                    <Col key={game._id} xs={20}>
                      <Game game={game} />
                    </Col>
                  ))}
                </ItemsCarousel>
              </Col>
            </Row>
          </>
        )}
      </Container>
    </>
  );
};

export default HomeScreen;
