import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  Form,
} from "react-bootstrap";
import Rating from "../components/Rating";
import { listGameDetails, createGameReview } from "../actions/gameActions";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { GAME_CREATE_REVIEW_RESET } from "../constatns/gameConstatns";

const GameScreen = ({ match, history }) => {
  const dispatch = useDispatch();
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const gameReviewCreate = useSelector((state) => state.gameReviewCreate);
  const { success: successGameReview, error: errorGameReview } =
    gameReviewCreate;

  const gameDetails = useSelector((state) => state.gameDetails);
  const { loading, error, game } = gameDetails;

  useEffect(() => {
    if (successGameReview) {
      alert("Review Submitted!");
      setRating(0);
      setComment("");
      dispatch({ type: GAME_CREATE_REVIEW_RESET });
    }
    dispatch(listGameDetails(match.params.id));
  }, [dispatch, match, successGameReview]);

  const buyNowHandler = () => {
    if (userInfo) {
      history.push(`/overview/${match.params.id}`);
    } else {
      history.push(`/login?redirect=overview/${game._id}`);
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createGameReview(match.params.id, { rating, comment }));
  };

  return (
    <>
      <Link
        className="btn btn-dark my-3"
        to="/"
        style={{
          textDecoration: "none",
        }}
      >
        Go back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <>
          <Row className="justify-content-center my-2">
            <Col md={8} lg={7} xs={12}>
              <Image
                src={game.image}
                alt={game.title}
                style={{ height: "100%", width: "100%" }}
                fluid
              />
            </Col>
            <Col md={4} sm={4} lg={3} xs={12} className="mt-2 mt-lg-4 mt-md-0">
              <Card>
                <ListGroup style={{ width: "100%" }}>
                  <ListGroup.Item
                    style={{ borderBottom: "solid", borderWidth: "0.5px" }}
                  >
                    <Row style={{ textAlign: "center" }}>
                      <Col as="h5">Price:</Col>
                      <Col as="h5">
                        <strong>${game.price}</strong>
                      </Col>
                    </Row>
                    <Row
                      as="h5"
                      className="mt-3 "
                      style={{ textAlign: "center" }}
                    >
                      <strong>Status:</strong>
                    </Row>
                    <Row as="h4" style={{ textAlign: "center" }}>
                      <strong>
                        {game.numberInStock > 0 ? "In Stock" : "Out Of Stock"}
                      </strong>
                    </Row>
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <Row>
                      <Button
                        onClick={buyNowHandler}
                        style={{
                          backgroundColor: "dodgerblue",
                          borderColor: "dodgerblue",
                        }}
                        type="button"
                        disabled={game.numberInStock === 0}
                      >
                        {game.numberInStock > 0 ? "Buy now" : "Out Of Stock"}
                      </Button>
                    </Row>
                  </ListGroup.Item>
                </ListGroup>
              </Card>
            </Col>
          </Row>
          <Row className="justify-content-center">
            <Col lg={10}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroup.Item
                    style={{ borderBottom: "solid", borderWidth: "0.5px" }}
                  >
                    <h3>{game.title}</h3>
                    <Rating
                      value={game.rating}
                      text={`${game.numReviews} reviews`}
                    />
                  </ListGroup.Item>
                  <ListGroup.Item
                    style={{ borderBottom: "solid", borderWidth: "0.5px" }}
                  >
                    {game.description}
                  </ListGroup.Item>
                  <ListGroup.Item>
                    {/* <Row className="justify-content-center">
                      <Col
                        lg={5}
                        style={{ borderRight: "solid", borderWidth: "0.5px" }}
                      >
                        <ul>
                          <strong>MINIMUM:</strong>
                          {game.minRequirements.map((requirement) => (
                            <li key={requirement}>{requirement}</li>
                          ))}
                        </ul>
                      </Col>
                      <Col lg={5}>
                        <ul>
                          <strong>RECOMMENDED:</strong>
                          {game.recRequirements.map((requirement) => (
                            <li key={requirement}>{requirement}</li>
                          ))}
                        </ul>
                      </Col>
                    </Row> */}
                  </ListGroup.Item>
                </ListGroup>
              </Card>
            </Col>
          </Row>
          <Row
            className="justify-content-center my-3 py-3"
            style={{ borderTop: "solid", borderWidth: "0.5px" }}
          >
            <Col lg={6}>
              <h2 className="text-center">Reviews</h2>
              {game.reviews.length === 0 && (
                <Message variant="warning">No Reviews</Message>
              )}
              <>
                <ListGroup variant="flush">
                  {game.reviews.map((review) => (
                    <ListGroup.Item
                      key={review._id}
                      style={{ borderBottom: "solid", borderWidth: "0.5px" }}
                    >
                      <h5>{review.name}</h5>
                      <Rating value={review.rating} />
                      <p>{review.createdAt.substring(0, 10)}</p>
                      <p>{review.comment}</p>
                    </ListGroup.Item>
                  ))}
                  <ListGroup.Item>
                    <h2>Write a Review</h2>
                    {errorGameReview && (
                      <Message variant="danger">{errorGameReview}</Message>
                    )}
                    {userInfo ? (
                      <Form onSubmit={submitHandler}>
                        <Form.Group controlId="rating" className="my-3">
                          <Form.Label>Rating</Form.Label>
                          <Form.Select
                            className="w-25"
                            value={rating}
                            onChange={(e) => e.SetRating(e.target.value)}
                          >
                            <option value="" style={{ color: "black" }}>
                              Select...
                            </option>
                            <option value="1" style={{ color: "black" }}>
                              1 - Poor
                            </option>
                            <option value="2" style={{ color: "black" }}>
                              2 - Fair
                            </option>
                            <option value="3" style={{ color: "black" }}>
                              3 - Good
                            </option>
                            <option value="4" style={{ color: "black" }}>
                              4 - Very Good
                            </option>
                            <option value="5" style={{ color: "black" }}>
                              5 - Excellent
                            </option>
                          </Form.Select>
                        </Form.Group>
                        <Col>
                          <Form.Group controlId="comment" className="my-3">
                            <Form.Control
                              stye={{ border: "solid red" }}
                              type="text"
                              as="textarea"
                            ></Form.Control>
                          </Form.Group>
                        </Col>
                        <Button type="submit" variant="primary">
                          Submit
                        </Button>
                      </Form>
                    ) : (
                      <Message>
                        Please{" "}
                        <Link
                          to={`/login?redirect=game/${game._id}`}
                          style={{ color: "black" }}
                        >
                          Sign in
                        </Link>{" "}
                        to write a review
                      </Message>
                    )}
                  </ListGroup.Item>
                </ListGroup>
              </>
            </Col>
          </Row>
        </>
      )}
    </>
