import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Col, Row, Carousel, Image, Container } from "react-bootstrap";
import Game from "../components/Game";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { Link, useParams } from "react-router-dom";
import { listGames } from "../actions/gameActions";
import Paginate from "../components/Paginate";

const HomeScreen = ({ match }) => {
  const dispatch = useDispatch();
  const { keyword, pageNumber } = useParams();

  // const keyword = match.params.keyword;
  // const pageNumber = match.params.pageNumber || 1;

  const gameList = useSelector((state) => state.gameList);
  const { loading, error, games, page, pages } = gameList;
  const discountGames = [];
  games.forEach((game) => (game.price <= 10 ? discountGames.push(game) : null));
  useEffect(() => {
    dispatch(listGames(keyword, pageNumber));
  }, [dispatch, keyword, pageNumber]);

  return (
    <>
      <h1>Latest Games</h1>
      <Container>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <>
            <Row className="justify-content-center">
              <Col>
                <Carousel>
                  {discountGames.map((game) => (
                    <Carousel.Item key={game._id}>
                      <Link
                        to={`/game/${game._id}`}
                        style={{ textDecoration: "none" }}
                      >
                        <Image
                          src={game.image}
                          alt={game.title}
                          className="d-block w-100"
                          fluid
                        />
                      </Link>
                    </Carousel.Item>
                  ))}
                </Carousel>
              </Col>
            </Row>
            <Row>
              {games.map((game) => (
                <Col key={game._id} sm={12} md={6} lg={4} xl={3}>
                  <Game game={game} />
                </Col>
              ))}
            </Row>
            <Paginate
              pages={pages}
              page={page}
              keyword={keyword ? keyword : ""}
              isAdmin="false"
            />
          </>
        )}
      </Container>
    </>
  );
};

export default HomeScreen;
