import React, { useEffect, useRef } from "react";
import { Col, ListGroup, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Game from "../components/Game";
import { getGameGenres, listGames } from "../actions/gameActions";
import Loader from "../components/Loader";
import Carousel from "react-multi-carousel";
import useDynamicRefs from "use-dynamic-refs";

const AllGamesScreen = () => {
  const gameList = useSelector((state) => state.gameList);
  const { loading, error, games } = gameList;
  const [getRef, setRef] = useDynamicRefs();

  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 4,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 1000, min: 400 },
      items: 1,
    },
  };

  const gameGenres = useSelector((state) => state.gameGenres);
  const { genres } = gameGenres;

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listGames(""));
    dispatch(getGameGenres());
  }, [dispatch]);

  const scrollToRef = (ref) => window.scrollTo(0, ref.current.offsetTop);
  const executeScroll = (ref) => scrollToRef(ref);

  return (
    <div>
      <Row className="justify-content-center">
        <Col sm={4} className="py-lg-4">
          <ListGroup horizontal="lg">
            {genres.map((genre) => (
              <ListGroup.Item
                onClick={() => executeScroll(genre)}
                style={{
                  cursor: "pointer",
                  textAlign: "center",
                  fontSize: 15,
                }}
                key={genre._id}
                action
                variant="dark"
              >
                {genre.name}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
      {loading && <Loader />}
      <Row>
        <Col>
          {genres.map((genre) => (
            <Row
              style={{ borderBottom: "solid", borderWidth: "0.5px" }}
              className="justify-content-center"
              ref={setRef(genre)}
              key={genre._id}
            >
              <h4 className="text-center py-2">{genre.name}</h4>
              <Carousel
                className="w-100"
                responsive={responsive}
                renderButtonGroupOutside={true}
                infinite={true}
                slidesToSlide={4}
              >
                {games.map(
                  (game) =>
                    game.genre._id === genre._id && (
                      <Col key={game._id}>
                        <Game game={game} />
                      </Col>
                    )
                )}
              </Carousel>
            </Row>
          ))}
        </Col>
      </Row>
    </div>
  );
};

export default AllGamesScreen;
