import React, { useEffect } from "react";
import { Card, Col, ListGroup, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Game from "../components/Game";
import { getGameGenres, listGames } from "../actions/gameActions";
import Loader from "../components/Loader";
import Carousel from "react-multi-carousel";

const AllGamesScreen = () => {
  const gameList = useSelector((state) => state.gameList);
  const { loading, error, games } = gameList;

  const gameGenres = useSelector((state) => state.gameGenres);
  const { genres } = gameGenres;

  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 4,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(listGames(""));
    dispatch(getGameGenres());
  }, [dispatch]);

  return (
    <div>
      <Row>
        <Col
          lg={3}
          style={{
            marginRight: "5%",
            paddingTop: "10%",
            paddingRight: "5%",
          }}
        >
          <ListGroup>
            {genres.map((genre) => (
              <ListGroup.Item
                style={{ cursor: "pointer" }}
                key={genre._id}
                action
                variant="dark"
              >
                {genre.name}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
        {loading && <Loader />}
        <Col>
          {genres.map((genre) => (
            <Row
              style={{ borderBottom: "solid", borderWidth: "0.5px" }}
              className="justify-content-center"
            >
              <h4>{genre.name}</h4>
              <Carousel
                className="w-100"
                responsive={responsive}
                renderButtonGroupOutside={true}
                infinite={true}
                slidesToSlide={4}
              >
                {games.map(
                  (game) =>
                    game.genre._id === genre._id && (
                      <Col lg={4} key={game._id}>
                        <Game game={game} />
                      </Col>
                    )
                )}
              </Carousel>
            </Row>
          ))}
        </Col>
      </Row>
    </div>
  );
};

export default AllGamesScreen;
