import axios from "axios";
import React, { useEffect, useState } from "react";
import { PayPalButton } from "react-paypal-button-v2";
import { Collapse, Card, Col, Row, Button, Dropdown } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getOrderDetails, payOrder } from "../actions/orderActions";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { ORDER_PAY_RESET } from "../constatns/orderConstants";
import { removeFromCart } from "../actions/cartActions";

const OrderScreen = ({ match, history }) => {
  const [open, setOpen] = useState(false);
  const [sdkReady, setSdkReady] = useState();
  const orderId = match.params.id;
  const dispatch = useDispatch();

  const orderDetails = useSelector((state) => state.orderDetails);
  const { order, loading, error } = orderDetails;

  const orderPay = useSelector((state) => state.orderPay);
  const { loading: loadingPay, success: successPay } = orderPay;

  useEffect(() => {
    const addPayPalScript = async () => {
      const { data: clientId } = await axios.get("/api/config/paypal");
      const script = document.createElement("script");
      script.type = "text/javascript";
      script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`;
      script.async = true;
      script.onload = () => {
        setSdkReady(true);
      };

      document.body.appendChild(script);
    };

    if (!order || successPay) {
      dispatch({ type: ORDER_PAY_RESET });
      dispatch(getOrderDetails(orderId));
    } else if (!order.isPaid) {
      if (!window.paypal) {
        addPayPalScript();
      } else {
        setSdkReady(true);
      }
    }
    if (successPay) {
      dispatch(removeFromCart(order.orderItem.game));
    }
  }, [dispatch, orderId, successPay, order]);

  const successPaymentHandler = (paymentResult) => {
    console.log(paymentResult);
    dispatch(payOrder(orderId, paymentResult));
  };

  const handleBackToHome = () => {
    history.replace("/");
  };

  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <>
      <Button variant="dark" onClick={handleBackToHome}>
        Back to Homepage
      </Button>
      <Row className="justify-content-center">
        <Col lg={5}>
          <Card style={{ backgroundColor: "indigo" }}>
            <Row className="justify-content-center" variant="flush">
              <Col
                lg={10}
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
              >
                <h3>Order: {order._id}</h3>
              </Col>
            </Row>
            <Row className="justify-content-center">
              <Col
                lg={4}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                Product:{" "}
              </Col>
              <Col
                lg={6}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                {" "}
                {order.orderItem.title}{" "}
              </Col>
            </Row>
            <Row className="justify-content-center">
              <Col
                lg={4}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                Account:{" "}
              </Col>
              <Col
                lg={6}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                {" "}
                {order.user.name}{" "}
              </Col>
            </Row>
            <Row className="justify-content-center">
              <Col
                lg={4}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                Price:{" "}
              </Col>
              <Col
                lg={6}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                {" "}
                ${order.orderItem.price}{" "}
              </Col>
            </Row>
            <Row className="justify-content-center">
              <Col
                lg={4}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                Payment Method:{" "}
              </Col>
              <Col
                lg={6}
                className="py-2"
                style={{
                  borderBottom: "solid",
                  borderWidth: "0.5px",
                  textAlign: "center",
                }}
                as="h5"
              >
                {" "}
                {order.paymentMethod}{" "}
              </Col>
            </Row>
            {!order.isPaid ? (
              <Row className="justify-content-center py-2">
                <Col lg={11} style={{ textAlign: "center" }} as="h5">
                  {loadingPay && <Loader />}
                  {!sdkReady ? (
                    <Loader />
                  ) : (
                    <PayPalButton
                      amount={order.totalPrice}
                      onSuccess={successPaymentHandler}
                    />
                  )}
                </Col>
              </Row>
            ) : (
              <Row className="justify-content-center py-2">
                <Col lg={11} style={{ textAlign: "center" }} as="h5">
                  <Row className="justify-content-center">
                    <Col
                      lg={7}
                      className="py-2"
                      style={{
                        textAlign: "center",
                      }}
                    >
                      <Button
                        variant="dark"
                        style={{
                          width: "100%",
                        }}
                        onClick={() => setOpen(!open)}
                        aria-controls="example-collapse-text"
                        aria-expanded={open}
                      >
                        Click to see your product key
                      </Button>
                      <Collapse in={open} className="my-1 ">
                        <Dropdown>
                          <Card.Header>
                            <p>{order.productKey}</p>
                          </Card.Header>
                        </Dropdown>
                      </Collapse>
                    </Col>
                  </Row>
                </Col>
              </Row>
            )}
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default OrderScreen;
