import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  Container,
} from "react-bootstrap";
import Rating from "../components/Rating";
import axios from "axios";

const ProductScreen = ({ match }) => {
  const [product, setProduct] = useState({});

  useEffect(() => {
    const fetchProduct = async () => {
      const { data } = await axios.get(`/api/games/${match.params.id}`);

      setProduct(data);
    };
    fetchProduct();
  }, [match.params.id]);

  return (
    <Container>
      <Link
        className="btn btn-light my-3"
        to="/"
        style={{ textDecoration: "none" }}
      >
        Go back
      </Link>
      <Row className="justify-content-md-center">
        <Col md={6}>
          <Image src={product.image} alt={product.title} fluid />
        </Col>
        <Card style={{ width: "18rem", height: "20rem", margin: "1rem" }}>
          <Col
            md={2}
            className="px-3 my-3"
            style={{ width: "auto", textAlign: "center" }}
          >
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Row>
                  <Col as="h5">Price:</Col>
                  <Col as="h5">
                    <strong>${product.price}</strong>
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col as="h5">Status:</Col>
                  <Col as="h5">
                    <strong>
                      {product.numberInStock > 0 ? "In Stock" : "Out Of Stock"}
                    </strong>
                  </Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Button
                    className="btn-block"
                    type="button"
                    disabled={product.numberInStock === 0}
                  >
                    {product.numberInStock > 0 ? "Add To Cart" : "Out Of Stock"}
                  </Button>
                </Row>
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Card>
      </Row>
      <Row className="justify-content-md-center">
        <Col lg={10}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h3>{product.title}</h3>
            </ListGroup.Item>
            <ListGroup.Item>
              <Rating
                value={product.rating}
                text={`${product.numReviews} reviews`}
              />
            </ListGroup.Item>
            <ListGroup.Item>Description: {product.description}</ListGroup.Item>
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
};

export default ProductScreen;
