    <div>
      <Row>
        <Col lg={2}>
          <ListGroup>
            <ListGroup.Item>1</ListGroup.Item>
            <ListGroup.Item>2</ListGroup.Item>
            <ListGroup.Item>3</ListGroup.Item>
            <ListGroup.Item>4</ListGroup.Item>
          </ListGroup>
        </Col>
        <Col>
          <Row style={{ borderBottom: "solid", borderWidth: "0.5px" }}>
            <Game />
          </Row>
          <Row style={{ borderBottom: "solid", borderWidth: "0.5px" }}>
            <Card>
              <Card.Img src="/images/bf5.jpg" />
            </Card>
          </Row>
          <Row style={{ borderBottom: "solid", borderWidth: "0.5px" }}>
            <Card>
              <Card.Img src="/images/acbf.jpg" />
            </Card>
          </Row>
        </Col>
      </Row>
    </div>
  );
};

export default AllGamesScreen;
